mapper.update:
update t_sys_pwd_findback set used = 'YES' 
where USERID = #{userId,jdbcType=VARCHAR} 
and CAPTCHA = #{captcha,jdbcType=VARCHAR}

mapper.select:
		select 
		from t_sys_pwd_findback 
		
			
				and USERID = #{userId,jdbcType=VARCHAR}
      		
      		
				and CAPTCHA = #{captcha,jdbcType=VARCHAR}
